import React, { Component } from 'react'

class Todo extends Component {
  constructor(){
    super();
    this.state = {
      todo: {
        name: '',
        time_to_complete: '',
        level_of_difficulty: '',
        completed: null
      },
      style: 'display: none'
  
    }
  }

  componentDidMount(){
      this.setState({
        name: this.props.props.name,
        time_to_complete: this.props.props.time_to_complete,
        level_of_difficulty: this.props.props.level_of_difficulty
      })
  }

  handleChange = (event) => {
    event.preventDefault();
    this.setState({
      completed: true
    })
    this.props.updateTodo(this.state)
  }

  render() {
    return(
      <>
      <li><input type="checkbox" onClick={this.handleChange} />{this.props.props.name}</li><button id="todobutton"style={{display: 'none'}}>Mark as Completed?</button>
      </>
    )
  }

}

export default Todo;
