import React, { Component } from 'react'
import { connect } from 'react-redux'
import TodoInput from '../components/TodoInput'
import Todo from '../components/Todo'
import { bindActionCreators } from 'redux';
import { fetchTodos, postTodo, updateTodo, deleteTodo} from '../actions/todoActions'

class TodosContainer extends Component {
  constructor(){
    super();
    this.state = {
      todos: []
    }
  }

  componentDidMount() {
    this.props.fetchTodos;
    this.setState({
      todos: this.props.todos
    })
  }

  filterTodos = () => {
    let todos = this.state.todos;
    todos = todos.map((todo) => <Todo props={todo} updateTodo={this.props.updateTodo} deleteTodo={this.props.deleteTodo} />)
    return todos
  }
  
  render() {
    return(
      <div className="column">
        Todos:
        {this.filterTodos()}
        <br />
        <br />
        <TodoInput postTodo={this.props.postTodo} />
      </div>
    )
  }
}

const mapStateToProps = (state) => {
  return {
    todos: state.todos
  }
}

const mapDispatchToProps = (dispatch) => {
  return bindActionCreators({
    fetchTodos: fetchTodos,
    postTodo: postTodo,
    updateTodo: updateTodo,
    deleteTodo: deleteTodo
  }, dispatch);
}



export default connect(mapStateToProps, mapDispatchToProps)(TodosContainer);
