import React, { Component } from 'react'
import { connect } from 'react-redux'
import TodoInput from '../components/TodoInput'
import Todo from '../components/Todo'

class TodosContainer extends Component {

  constructor() {
    super();
    this.state = {
      todos: []
    }
  }

  //renderTodos = () => this.props.todos.map((todo, i) => <Todo key={i} name={todo.name} />)

  componentDidMount(){
    return fetch('api/todo')
    .then(results => {
      return results.json();
    }).then(data => {
      let todos = data.results.map((todo) => {
        return(
          <div>{todo.name}</div>
        )
      })
    })
  }

  render() {
    return(
      <div>
        <TodoInput addTodo={this.props.addTodo} />
      </div>
    )
  }
}

const mapStateToProps = state => {
  return {
    todos: state.todos
  }
}

const mapDispatchToProps = (dispatch) => ({addTodo: todo => dispatch({type: 'ADD_TODO', todo}) })

export default connect(mapStateToProps, mapDispatchToProps)(TodosContainer);
